DROP TABLE IF EXISTS candgovernors;
CREATE TABLE candgovernors (
   governor_id smallint(5) unsigned NOT NULL auto_increment,
   lastname varchar(25) NOT NULL,
   firstname varchar(25) NOT NULL,
   middleinitial char(1),
   party_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   province_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   picturelocation varchar(60) NOT NULL,
   birthdate date DEFAULT '0000-00-00' NOT NULL,
   educattainment text NOT NULL,
   accomplishments text NOT NULL,
   platform text NOT NULL,
   workexperiences text NOT NULL,
   familyinfo text NOT NULL,
   biography text NOT NULL,
   birthplace varchar(30) NOT NULL,
   emailaddr varchar(50) NOT NULL,
   faxnum varchar(30) NOT NULL,
   telnum varchar(30) NOT NULL,
   civilstatus_id tinyint(4) DEFAULT '0' NOT NULL,
   timestamp timestamp(14),
   programofgovt text NOT NULL,
   standonissues text NOT NULL,
   nickname varchar(20) NOT NULL,
   adpaidby varchar(80) NOT NULL,
   numvotes int(10) unsigned DEFAULT '0' NOT NULL,
   coalition_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   is_proclaimed enum('Y','N') DEFAULT 'N' NOT NULL,
   activities text NOT NULL,
   PRIMARY KEY (governor_id),
   UNIQUE lastname (lastname, firstname, middleinitial)
);

