DROP TABLE IF EXISTS candsenators;
CREATE TABLE candsenators (
   senator_id smallint(5) unsigned NOT NULL auto_increment,
   lastname varchar(25) NOT NULL,
   firstname varchar(25) NOT NULL,
   middleinitial char(1),
   party_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   picturelocation varchar(60) NOT NULL,
   birthdate date DEFAULT '0000-00-00' NOT NULL,
   educattainment text NOT NULL,
   accomplishments text NOT NULL,
   platform text NOT NULL,
   workexperiences text NOT NULL,
   familyinfo text NOT NULL,
   biography text NOT NULL,
   birthplace varchar(30) NOT NULL,
   emailaddr varchar(50) NOT NULL,
   faxnum varchar(30) NOT NULL,
   telnum varchar(30) NOT NULL,
   civilstatus_id tinyint(4) DEFAULT '0' NOT NULL,
   timestamp timestamp(14),
   programofgovt text NOT NULL,
   standonissues text NOT NULL,
   nickname varchar(20) NOT NULL,
   adpaidby varchar(80) NOT NULL,
   numvotes int(10) unsigned DEFAULT '0' NOT NULL,
   coalition_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   numvotesunof int(10) unsigned DEFAULT '0' NOT NULL,
   is_proclaimed enum('Y','N') DEFAULT 'N' NOT NULL,
   region1votes int(10) unsigned DEFAULT '0' NOT NULL,
   region2votes int(10) unsigned DEFAULT '0' NOT NULL,
   region3votes int(10) unsigned DEFAULT '0' NOT NULL,
   region4votes int(10) unsigned DEFAULT '0' NOT NULL,
   region5votes int(10) unsigned DEFAULT '0' NOT NULL,
   region6votes int(10) unsigned DEFAULT '0' NOT NULL,
   region7votes int(10) unsigned DEFAULT '0' NOT NULL,
   region8votes int(10) unsigned DEFAULT '0' NOT NULL,
   region9votes int(10) unsigned DEFAULT '0' NOT NULL,
   region10votes int(10) unsigned DEFAULT '0' NOT NULL,
   region11votes int(10) unsigned DEFAULT '0' NOT NULL,
   region12votes int(10) unsigned DEFAULT '0' NOT NULL,
   regionCARvotes int(10) unsigned DEFAULT '0' NOT NULL,
   regionARMMvotes int(10) unsigned DEFAULT '0' NOT NULL,
   regionNCRvotes int(10) unsigned DEFAULT '0' NOT NULL,
   region13votes int(10) unsigned DEFAULT '0' NOT NULL,
   Absenteevotes int(10) unsigned DEFAULT '0' NOT NULL,
   activities text NOT NULL,
   PRIMARY KEY (senator_id),
   UNIQUE lastname (lastname, firstname, middleinitial)
);

