   url varchar(100) NOT NULL,
   title varchar(75) NOT NULL,
   president_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   vicepresident_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   senator_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   representative_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   governor_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   vicegovernor_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   provboardmember_id smallint(5) unsigned DEFAULT '0' NOT NULL,
