   vicemayor_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   councilor_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candpresident_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candvicepresident_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candsenator_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candrepresentative_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candgovernor_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candvicegovernor_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   candboardmem_id smallint(5) unsigned DEFAULT '0' NOT NULL,
