   municity_id smallint(5) unsigned NOT NULL auto_increment,
   name varchar(30) NOT NULL,
   legdist_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   factsheet text NOT NULL,
   numbarangays smallint(5) unsigned DEFAULT '0' NOT NULL,
   nummaleregvoters int(10) unsigned DEFAULT '0' NOT NULL,
   numfemaleregvoters int(10) unsigned DEFAULT '0' NOT NULL,
   landarea smallint(5) unsigned DEFAULT '0' NOT NULL,
   numregvoters int(10) unsigned DEFAULT '0' NOT NULL,
   numprecincts int(10) unsigned DEFAULT '0' NOT NULL,
   is_maydispresult enum('Y','N') DEFAULT 'N' NOT NULL,
   is_vmaydispresult enum('Y','N') DEFAULT 'N' NOT NULL,
   is_coundispresult enum('Y','N') DEFAULT 'N' NOT NULL,
   mayresultcomment tinytext NOT NULL,
   vmayresultcomment tinytext NOT NULL,
   counresultcomment tinytext NOT NULL,
   numactualvoters int(10) unsigned DEFAULT '0' NOT NULL,
   PRIMARY KEY (municity_id),
   UNIQUE name (name, legdist_id)
);

