DROP TABLE IF EXISTS party;
CREATE TABLE party (
   party_id smallint(5) unsigned NOT NULL auto_increment,
   name varchar(250) NOT NULL,
   acronym varchar(30),
   yearfounded smallint(5) unsigned DEFAULT '0',
   is_national enum('Y','N') DEFAULT 'N' NOT NULL,
   province_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   partytype_id tinyint(4) unsigned DEFAULT '0' NOT NULL,
   municity_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   ncrmunicity_id smallint(5) unsigned DEFAULT '0' NOT NULL,
   vision text NOT NULL,
   mission text NOT NULL,
   platform text NOT NULL,
   party_history text NOT NULL,
   partyorganization text NOT NULL,
   email varchar(50) NOT NULL,
   telnumbers varchar(30) NOT NULL,
   address varchar(150) NOT NULL,
   faxnumbers varchar(30) NOT NULL,
   timestamp timestamp(14),
   programofgovt text NOT NULL,
   standonissues text NOT NULL,
   yearregistered smallint(5) unsigned DEFAULT '0' NOT NULL,
   PRIMARY KEY (party_id),
   UNIQUE name (name, acronym, partytype_id)
);

