INSERT INTO candmayors VALUES (5,'Liban','Dante','V',465,0,5,'candmayor - dante liban.jpg','0000-00-00','[Source: <A HREF=\"http://www.liban.faithweb.com/profile.htm\">http://www.liban.faithweb.com/profile.htm</A>]<BR><BR>\r\n<UL>\r\n<LI>John F. Kennedy Schools of Government, Harvard University - \"Executive Programme for Leaders in Development\"\r\n \r\n<LI>University of the Philippines Entrance Scholar, College Scholar and Jovito R. Salonga Scholar\r\n \r\n<LI>Bachelor of Arts in Public Administration, Masters in Public Adminitration and Bachelor of Laws\r\n \r\n<LI>Doctor of Laws honoris causa given by the International Academy of Leadership and the Pangasinan State University\r\n \r\n<LI>Doctor of Humanities honoris causa given by the Central Luzon State University, the Univeristy of Northern Philippines, the Don Mariano Marcos State University, and the Cagayan State University\r\n \r\n<LI>Doctor of Public Administration honoris causa given by the Tarlac State University\r\n \r\n<LI>Doctor of Public Management honoris causa given by\r\n \r\n<LI>Regent, University of the Philippines Board of Regents\r\n \r\n<LI>Regent, Mindanao State University Board of Regents and all other state universities.\r\n \r\n</UL>','[Source: <A HREF=\"http://www.liban.faithweb.com/performance.htm\">http://www.liban.faithweb.com/performance.htm</A>]<BR><BR>\r\n<FONT size=2><SPAN \r\nstyle=\"FONT-FAMILY: Wingdings; mso-bidi-font-size: 12.0pt\">ü<SPAN \r\nstyle=\"FONT-WEIGHT: normal; FONT-STYLE: normal; FONT-FAMILY: Times New Roman; FONT-VARIANT: normal\">&nbsp;</SPAN></SPAN></FONT>\r\n<FONT \r\nsize=2><B><I><SPAN style=\"COLOR: green; mso-bidi-font-size: 12.0pt\">PRO FAMILY, \r\nPRO LIFE, PRO-GOD </SPAN></I></B><SPAN \r\nstyle=\"mso-bidi-font-size: 12.0pt\"><O:P></O:P></SPAN></FONT></P>\r\n<P \r\nstyle=\"MARGIN-LEFT: 0.35in; TEXT-INDENT: -0.1in; TEXT-ALIGN: justify; mso-list: l0 level1 lfo11; tab-stops: list .35in\"><FONT \r\nsize=2><SPAN style=\"FONT-FAMILY: Symbol; mso-bidi-font-size: 12.0pt\">·<SPAN \r\nstyle=\"FONT-WEIGHT: normal; FONT-STYLE: normal; FONT-FAMILY: Times New Roman; FONT-VARIANT: normal\">&nbsp;&nbsp; \r\n</SPAN></SPAN><SPAN style=\"mso-bidi-font-size: 12.0pt\">principal author opposing \r\ndomestic violence, child abuse and actively campaigned against anti-family \r\nbills.<O:P> </O:P></SPAN></FONT></P>\r\n<P style=\"MARGIN-LEFT: 0.25in; TEXT-INDENT: -0.25in; TEXT-ALIGN: justify\"><FONT \r\nsize=2><SPAN style=\"mso-bidi-font-size: 12.0pt\">&nbsp;<O:P> </O:P></SPAN><SPAN \r\nstyle=\"FONT-FAMILY: Wingdings; mso-bidi-font-size: 12.0pt\">ü<SPAN \r\nstyle=\"FONT-WEIGHT: normal; FONT-STYLE: normal; FONT-FAMILY: Times New Roman; FONT-VARIANT: normal\">&nbsp; \r\n</SPAN></SPAN><B><I><SPAN \r\nstyle=\"COLOR: green; mso-bidi-font-size: 12.0pt\">ENVIRONMENTAL \r\nCHAMPION</SPAN></I></B><SPAN style=\"mso-bidi-font-size: 12.0pt\"> \r\n<O:P></O:P></SPAN></FONT></P>\r\n<P \r\nstyle=\"MARGIN-LEFT: 0.35in; TEXT-INDENT: -0.1in; TEXT-ALIGN: justify; mso-list: l6 level1 lfo12; tab-stops: list .35in\"><FONT \r\nsize=2><SPAN style=\"FONT-FAMILY: Symbol; mso-bidi-font-size: 12.0pt\">·<SPAN \r\nstyle=\"FONT-WEIGHT: normal; FONT-STYLE: normal; FONT-FAMILY: Times New Roman; FONT-VARIANT: normal\">&nbsp;&nbsp; \r\n</SPAN></SPAN><SPAN style=\"mso-bidi-font-size: 12.0pt\">advocated for the \r\nimmediate closure of the Payatas dumpsite since 1992<O:P> \r\n</O:P></SPAN></FONT></P>\r\n<P \r\nstyle=\"MARGIN-LEFT: 0.35in; TEXT-INDENT: -0.1in; TEXT-ALIGN: justify; mso-list: l6 level1 lfo12; tab-stops: list .35in\"><FONT \r\nsize=2><SPAN style=\"FONT-FAMILY: Symbol; mso-bidi-font-size: 12.0pt\">·<SPAN \r\nstyle=\"FONT-WEIGHT: normal; FONT-STYLE: normal; FONT-FAMILY: Times New Roman; FONT-VARIANT: normal\">&nbsp;&nbsp; \r\n</SPAN></SPAN><SPAN style=\"mso-bidi-font-size: 12.0pt\">authored the Solid Waste \r\nManagement Act and the Clean Air Act for a cleaner and greener Quezon City. \r\nCampaigned for total logging ban.<O:P> </O:P></SPAN></FONT></P>\r\n<P style=\"TEX