INSERT INTO candsenators VALUES (5,'Chato','Liwayway','V',465,'candsenator - liwayway chato.jpg','1945-12-31','','[Source: <A HREF=\"http://www.chato.ph/bin/profile.cfm\">http://www.chato.ph/bin/profile.cfm</A>]<BR><BR>\r\n<UL>\r\n<LI>Lawyer \r\n<LI>Bachelor of Arts, Magna Cum Laude, University of Santo Tomas (1964) \r\n<LI>Bachelor of Laws, University of the Philippines (1968) \r\n<LI>BIR Commissioner (1993 - 1998) \r\n<LI>Member, Couples for Christ \r\n<LI>One of Five Outstanding UP Coeds (1967) \r\n<LI>One of 12 Outstanding Women in Government - National press Club (1998) \r\n<LI>One of the Outstanding Filipino Women - National Centennial Commission (1998) \r\n<LI>From DAET, CAMARINES NORTE (BICOL) \r\n</UL>','[Source: <A HREF=\"http://www.chato.ph/bin/platform.cfm\">http://www.chato.ph/bin/platform.cfm</A>]<BR><BR>\r\n<A name=1><FONT face=Arial color=#990000 size=3><B>TAX REFORMS</B></FONT></A> \r\n<BR><BR><FONT face=Arial color=#000000 size=-1><B>\r\n<LI>Introduce amendments to simplify existing tax laws to increase revenues. \r\n<LI>Spearhead the move of government to adopt more socially equitable tax \r\nmeasures. \r\n<LI>Introduce measures that will reduce taxation on fuel used for mass transport \r\nand power generation. \r\n<LI>Introduce legislation transforming the Bureau of Internal Revenue (BIR) and \r\nthe Bureau of Customs (BOC) into more independent REVENUE AUTHORITIES. Thereby \r\npromoting non-partisanship and professionalization of the revenue-generating \r\nfunction of the national government. </B></FONT><BR><BR>\r\n\r\n<BR><BR><A \r\nname=2><FONT face=Arial color=#990000 size=3><B>EMPOWERMENT OF THE LESS \r\nPRIVILEGED</B></FONT></A> <BR><BR><FONT face=Arial color=#000000 size=-1><B>\r\n<LI>Author laws that will allow creative financing to source funds to jumpstart \r\nsmall and medium-scale industries. \r\n<LI>Lead in the abrogation of tariffs that disadvantage the less privileged. \r\n<LI>Sponsor humane housing program laws that will do away relocation of less \r\nprivileged families without the needed support mechanisms. \r\n<LI>Legislate laws that will protect the right of income-earners to equitable \r\ncompensation in accordance with humane living standards. \r\n<LI>Provide better economic opportunities for the less privileged farmers, \r\nfishermen and other rural folks. </B></FONT><BR><BR>\r\n\r\n <BR><BR><A \r\nname=3><FONT face=Arial color=#990000 size=3><B>YOUTH EMPOWERMENT</B></FONT></A> \r\n<BR><BR><FONT face=Arial color=#000000 size=-1><B>\r\n<LI>Sponsor laws to increase the share of education in the national budget, \r\nthus, protecting the right of our young to quality education. \r\n<LI>Institutionalize youth representation in various levels of both government \r\nand private sectors. \r\n<LI>Institutionalize Information Technology as an integral part of the country\'s \r\nsystem of education in all its levels to make the Filipino youth globally \r\ncompetitive. \r\n<LI>Provide for livelihood opportunities for out-of-school youth. \r\n</B></FONT><BR><BR>\r\n<BR><BR>\r\n<A name=4><FONT face=Arial color=#990000 size=3><B>EMPOWERMENT OF \r\nWOMEN</B></FONT></A> \r\n <BR><BR><FONT face=Arial \r\ncolor=#000000 size=-1><B>\r\n<LI>Legislate Equal Opportunity Laws for women in all sectors of society. \r\n<LI>Submit laws that will ensure equal rights for the Filipino women and provide \r\nher a share in the responsibility of nation building and reconciliation. \r\n<LI>Move to strengthen laws that protect Filipino women from all forms of \r\nviolence and harassment. \r\n<LI>Provide for affordable health services for women and children. \r\n</B></FONT><BR><BR>\r\n<BR><BR><A name=5><FONT face=Arial color=#990000 \r\nsize=3><B>LEVELLING OF THE BUSINESS PLAYING FIELD</B></FONT></A> <BR><BR><FONT \r\nface=Arial color=#000000 size=-1><B>\r\n<LI>Abolition of the system of cronyism. \r\n<LI>Abolition of business monopolies and cartels. \r\n<LI>Strengthen laws that secure the interest of domestic industries against \r\nprejudicial market condition. </B></FONT><BR><BR>\r\n <BR><BR><A name=6><FONT \r\nface=Arial color=#990000 size=3><B>Local Government</B></FONT></A> <BR><BR><FONT \r\nface=Arial color=#000000 size=-1><B>\r\n<LI>Increase Internal Revenue Allotment (IRA) for local government to 50 %. \r\n<LI>Give more power to local government units and promote greater autonomy at \r\nthe local level. </B></FONT><BR><BR><BR><BR><A name=7><FONT \r\nface=Arial color=#990000 size=3><B>Food and Energy Security</B></FONT></A> \r\n<BR><BR><FONT face=Arial color=#000000 size=-1><B>\r\n<LI>Provide incentives to agricultural research that will use indigenous and \r\nnatural means of increasing agricultural productivity and livestock production; \r\n<LI>Encourage group farming considering that the CARL only grants 3 has. to each \r\nfarmer beneficiary; \r\n<LI>Intensify livestock production through tax incentives. \r\n<LI>Develop and fully utilize geothermal energy which is most abundant in the \r\nPhilippines. </B></FONT><BR><BR> <BR><BR><A name=8><FONT face=Arial \r\ncolor=#990000 size=3><B>Encourage intellectual creativity of the Filipino \r\npeople</B></FONT></A> <BR><BR><FONT face=Arial color=#000000 size=-1><B>\r\n<LI>Provide incentives for intellectual creativity to the academe, corporate and \r\nindividual inventors. \r\n<LI>Provide credit facilities for development of commercial intellectual \r\ncommercial creations \r\n<LI>Hasten the patenting procedure. </B></FONT><BR><BR><BR></LI>','','','','Daet, Camarines Norte','info@chato.ph','','631-75-17, 631-75-18',2,20010531112842,'','','Liway','',4614569,1,3626652,'N',257332,47909,534470,481233,376442,341723,200464,60736,64259,128115,137105,14694,64855,0,287742,168279,94);
INSERT INTO candsenators VALUES (6,'Chavez','Melchor','G',249,'candsenator - melchor chaves.jpg','1951-08-06','','','','','','','Kidapawan, North Cotabato','','','',1,20010531112842,'','','Mel','',226936,0,253670,'N',20120,6033,13381,19923,9405,24161,10923,4750,3771,9193,14060,1915,9050,0,8122,9684,0);
INSERT INTO candsenators VALUES (7,'De Castro','Noli','L',465,'candsenator - noli de castro.jpg','1949-07-06','','','','','','','Pola, Oriental Mindoro','','','',2,20010531112842,'','<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>Mga Paninindigan ni \r\nKabayan Noli De Castro </B></FONT><BR><FONT face=\"Arial, Helvetica, sans-serif\" \r\nsize=2>(Mga prayoridad sa senado at paninindigan sa iba pang isyu) <BR><BR>1. \r\nIntroduction The role of the senate is not only to enact laws but also to \r\nreview, revise or repeal laws. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>2. Priority Legislative \r\nAgenda of NDC<BR></FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Karunungan, Kalikasan, \r\nKapangyarihan ng Mamamayan<BR></FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Karunungan <BR>Philosophy: \r\nEducation is the greatest equalizer of opportunities<BR>Issues: Access, Quality \r\nand Relevance</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>NDC Legislative Agenda \r\n:<BR>Improve Basic Education and Teachers Health Conditions </FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Integrate pre-school \r\neducation into public elementary school systems<BR></FONT>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Institutionalize Technical \r\nand Vocational Education in Senior High School</FONT> \r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Incorporate environmental \r\neducation in the high school curriculum</FONT> \r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Provide comprehensive \r\nmedical and dental care program for Teachers in view of high incidence of \r\ncommunicable diseases among public school teachers</FONT> </LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Kalikasan \r\nPhilosophy:</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Sustainable \r\nDevelopment</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Issues:Lack of focused \r\nmanpower (critical mass of people) to sustain environmental education, \r\nprotection, conservation and rehabilitation programs. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>NDC Legislative Agenda: \r\nInstitutionalize Youth Participation in Environmental Programs</FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Enact law converting \r\nSanguniang Kabataan into youth environmental corps in every barangay to be known \r\nas Green Brigade</FONT> </LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Kapangyarihan ng Mamamayan \r\n</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Philosophy: Sovereignity \r\nresides in the people and all authority emanates from them. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Issues: Political decisions \r\nare made in Manila NDC Legislative Agenda:<BR>Ensure greater people\'s \r\nparticipation in decision-making processes </FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Amend the Constitution to \r\nestablish Federal Form of Government </FONT>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Institutionalize the role \r\nof civil society in policy making of government from local to national \r\nagency.</FONT> </LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>3. …at mga paninidigan sa \r\niba pang isyu ni Kabayan </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>A. Charges against \r\nERAP<BR>Kung ano ang sinasabi ng batas, iyon ang dapat ipatupad. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>B. Media<BR>Kailangan na \r\nmagkaroon ng Magna Carta for Workers in Media na kasing tulad ng Magna Carta for \r\nPublic School Teachers. Kailangan ma-proteksionan ang mga manggagawa sa media \r\nlaban sa harashment tulad ng libel. Dapat may batas na kagyat na binabasura ng \r\nhukuman ang anumang libel case ng isang opisyal laban sa mamamahayag kung ang \r\ninihahabla ay tungkol sa pagbatikos o expose hinggil sa anomalyang \r\nkinasasankutan ng naturang opisyal. Kailangan na magkaroon din ng \r\nstandardization sa salary ng media workers upang maiiwas sila sa posibleng \r\nkorapsyon.</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>C. Problems with CPP/NPA/NDP \r\nand MNLF and MILF<BR>Ituloy ang peace talks.Dapat ang confidence building \r\nmeasures ay tuloy tuloy bago pa maantala ang peace talks sa mga teknikalidad \r\ntulad na kung saan gagawin ang usapan at sinu-sino ang bumubuo ng peace panels. \r\nSa ganang akin lang, ang anumang peace talks ay dapat nakapaloob sa framework ng \r\nconstitution ng Pilipinas. Ibig sabihin, non-negotiable na iisa lang ang Armed \r\nForces, iisa lang ang Constitution at iisa lang ang tetiroyo ng pilipinas at ito \r\nay hindi pwedeng paghatian ng ibat ibang rebel groups. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>D. Overseas Filipino \r\nWorkers<BR>Mahigit na $9 bilyon ang naiaambag sa bayan ng mga OFW\'s taon-taon. \r\nDapat pagkalooban ng technical o livelihood training ng Technical Education and \r\nSkills Development Authority (TESDA) ang mga naiiwang pamilya rito para \r\nmagkaroon sila ng livelihood projects sapagkat mayroon naman silang tiyak na \r\nmapagkukunan ng puhunan.</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>E. Farmers protection \r\nagainst globalization<BR>Hihilingin ko sa Department of Agriculture na palakasin \r\nang information at education program ng naihanda nilang safety nets para sa \r\nmagsasaka. Pero nainis kong makita na ang mga agricultural schools at chambers \r\nof commerce and industry sa mga lalawigan na magkaloob ng technical assistance \r\npara sa mga pagsasaka para silay maging globally competitive. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>F. Workers problem against \r\nglobalization<BR>Pinag-aaralan ko ang paglikha ng batas na tatawagin na \r\n\"Business-Workers Rescue Plan\". Na sa halip na isara ang kumpanya kung ito ay \r\nnalulugi na, bigyan ito ng anim na buwan pa para patakbuhin. Subalit, ang sweldo \r\nng mga mangagawa ay nakabatay na sa buwanang kita ng kumpanya. Kailangan din na \r\ntiyakin muna ng pamahalaan, tulad ng DTI na lugi na ang kumpanya at kailangan \r\ndin na maging transparent ang mga kumpanyang ito. Pero para maging globally \r\ncompetitive ang ating mga mangagawa sa industriya, services at manufacturing, \r\nkailangan na palakasin ang joint government and business technical assistance \r\npara sa periodic technical training ng mga managagagwa para sila maging globally \r\ncompetitive. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>G. Fight against corruption \r\nin government <BR>Sa Constitution natin, maliban sa AFP senior officers at \r\ncareer diplomats, ang mga cabinet members lang ang idinadaan sa Commission on \r\nAppointments. Pero iyong mga Heads of Agencies tulad ng LTO, Bureau of Customs, \r\nBIR, LTFRB, Marina, PTA, PAGCOR, PCSO, ATO, Civil Aeronotics Board ay hindi na. \r\nKadalasan, narito ang reklamo ng mga mamamayan. Kaya hihilingin ko ang \r\n\"Automatic Review\" ng Senate Blue Ribbon Committee hinggil sa integridad at \r\nkakayahan ng mga taong nailoloklok sa mga pwestong ito. Pangalawa, nais kong \r\nmagkaroon ng batas na awtomatikong ipaalam ng Ombudsman ang mga government \r\nofficials na nabibigyan ng promotion o mas mataas na pwesto. Higit sa lahat, \r\nnais kong makita na ang bawat ahensiya ng pamahalaan ay may \"Employees \r\nAssociation\" na magbabantay sa mga katiwalian ng kanilang mga opisyal at kasama. \r\n</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>H. Church versus \r\ngovernment<BR>Ginagalang ko ang separation of church and state . Subalit, nais \r\nkong makita ang simbahan na tumulong sa pamahalaan sa paghubog ng mga mamamayan \r\nna maka-dios at makabayan mula sa murang edad pa lamang. Sapagkat kong ang mga \r\nmatatandang opisyal na ng pamahalaan ang kanilang binabatikos, kadalasan ang mga \r\nito ay manhid na. </FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>I. Jueteng<BR>Kung ang bola \r\nng jueteng ay transparent, nasa public plaza o televized kaya tulad ng lotto, at \r\nkung tiyak na gobyerno ang magpapatakbo nito tulad ng ginagawa ng PAGCOR at kung \r\ntiyak na makikinabang ang mga local government units, payag ako na e-legalize \r\nito. Pero habang nandidiyan ang lotto, siguro pagtiyagaan na lang natin ang \r\nlotto.</FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2>J. Traffic problems in Metro \r\nManila<BR>Disiplina sa kalsada ang kailangan. At mga taong may disiplina ang \r\ndapat magpatupad dito. Nais kong makita na ang mga graduates ng ROTC na walang \r\ntrabaho ay kunin ng MMDA para magpatupad ng disiplina sa kalsada. \r\n</FONT><BR></P>','Kabayan','',15605121,2,12183754,'N',1167586,263900,1453423,2405436,754957,1127212,416202,213621,186750,510345,921515,210922,202820,0,784314,453483,303);
INSERT INTO candsenators VALUES (8,'Defensor Santiago','Miriam','P',238,'candsenator - miriam defensor santiago.jpg','1945-06-15','','[Source: <A HREF=\"http://miriam.com.ph/tperform.htm\">http://miriam.com.ph/tperform.htm</A>]<BR><BR>\r\n<P>\r\nSenator Santiago exposed the graft and corruption in the Department of Interior \r\nand Local Government and sought Senate blue ribbon committee probes on the \r\nstring of anomalous government contracts entered into by DILG secretary Ronaldo \r\nPuno, namely: \r\n<P>1.&nbsp; the P3.6 billion Mahogany drug-test deal; <BR>2.&nbsp; the P307 \r\nmillion handcuffs deal with Pelaez Enterprises; <BR>3.&nbsp; the P1 billion LAMP \r\ncontract; <BR>4.&nbsp; the P19.4 billion budget scam; <BR>5.&nbsp; the creation \r\nof the PNP Special Operation Force; and, <BR>6.&nbsp; the P40 million DILG \r\ninformation campaign. \r\n<P>&nbsp;The Senate blue ribbon committee later recommended the filing of graft \r\ncharges against Puno.&nbsp; A government-commissioned investigation also pointed \r\nto Puno as the perpetrator of a series of&nbsp; corrupt activities which \r\ncrippled the coffers of the national government.&nbsp; Again, if not for Sen. \r\nMDS, these anomalies would have gone unnoticed. \r\n<P>· Senator Santiago launched her Code Annotated Series Project 2000 which is \r\nintended to update legal knowledge for the 21st century. She annotated with the \r\nlatest Supreme Court decisions, all the major legal codes in the country.&nbsp; \r\nThe three new law books launched in mid-year were Constitution Annotated, Rules \r\nof Court Annotated and Penal Code Annotated.&nbsp; The books immediately hit the \r\nbestsellers’ list.&nbsp; Santiago later launched in December two more law books \r\nas part of the series, namely Civil Code Annotated and Local Government Code \r\nAnnotated. \r\n<P>· Senator Santiago launched her International Law (with Philippine Cases and \r\nMaterials and ASEAN Instruments).&nbsp; Santiago resided at Cambridge \r\nUniversity, as a 1999 Visiting Fellow of the Lauterpacht Research Center for \r\nInternational Law last summer where she was inspired at top speed to write her \r\ntreatise on international law. \r\n<P>· Senator Santiago delivered a privilege speech analyzing the criminal \r\nliability of Ramos and five of his cabinet secretaries for authorizing the \r\nrelease of P7 billion to propagandize the centennial celebrations.&nbsp; \r\nSantiago said the “smoking gun” in the proposed criminal case against former \r\npresident Fidel Ramos is the memorandum dated 27 February 1997 where Ramos \r\ndirected the executive departments to support from their 1997 budgets the \r\ncentennial celebration project.&nbsp; She charged that Ramos failed to comply \r\nwith the two conditions allowed by the Constitution for the transfer of \r\nappropriations, namely it should only be an augmentation and it should come from \r\nsavings.&nbsp; The Senate blue ribbon committee later released its report \r\nfinding Ramos and his cabinet secretaries liable for graft. The ex-president \r\nsuffered another crushing blow when the committee headed by Senator Rene \r\nSaguisag also found him guilty of graft. Santiago was instrumental in ensuring \r\nthat the highest public office will always be a public trust. \r\n<P>· Senator Santiago publicly disclosed the alleged practice of big-time casino \r\nVIPs of getting multi-million loans in state-owned casinos run by Pagcor which \r\nenables them to continue gambling even after they have lost all their \r\ncash.&nbsp; Santiago said that the government may have lost from P400 to P1 \r\nbillion, because of bouncing checks issued by casino VIPs.&nbsp; She stressed \r\nthat credit lines which remain unpaid for extended periods of time represent \r\nsubstantial revenue losses for the government. \r\n<P>· Senator Santiago introduced the country’s first and only legislative \r\ntraining program called Summer sa Senado.&nbsp; The program is designed to teach \r\nthe youth about legislation and gave them a hands-on training on drafting bills \r\nand resolutions.&nbsp; Students from the country’s top universities participated \r\nin the program and penned their own bills on different topics such as consumer \r\nrights, environment protection, and children’s rights.&nbsp; Santiago empowered \r\nthe youth by sponsoring the interns’ original bills in the Senate. \r\n<P>· Senator Santiago called for the speedy passage of her bill, “Code of \r\nProfessional Standards for Political Public Relations,” that seeks to \r\nprofessionalize the practice of political public relations and penalize bribery \r\nof media members in order to obtain preferential or guaranteed news or editorial \r\ncoverage. \r\n<P>· Senator Santiago voted for the Visiting Forces Agreement.&nbsp; Santiago \r\ncalled the VFA a “necessary imposition,” saying the Philippines needs to make a \r\nchoice between the US and Japan on the one hand, or China on the other \r\nhand.&nbsp; She said the Philippines should remain an ally of the US, because \r\nthe US “remains at the helm of the strongest network of military alliances in \r\nthe Asia-Pacific.” \r\n<P>· Senator Santiago delivered a statement at the United Nations General \r\nAssembly in Vienna, Austria, calling on the international community to consider \r\nthe adoption of the UN Declaration on the Guiding Principles of Drug Demand \r\nReduction as a potent measure against drug manufacturing and trafficking. \r\n<P>· Senator Santiago interceded with the DOTC secretary to re-open the Iloilo \r\nCity Airport in time with the Dinagyang Festival scheduled for January 17 to 24, \r\n1999.&nbsp; Santiago urged the DOTC to speed up the maintenance work and repairs \r\nneeded for the airport runway. \r\n<P>· Filed the most number of bills and resolutions in the Senate, \r\nwith&nbsp;&nbsp; bills and&nbsp;&nbsp; resolutions.&nbsp; Delivered 12 privilege \r\nspeeches:&nbsp; Media Corruption in the National Elections, The Charter Change \r\nis Unconstitutional, The Proposed VFA is Void Ab Initio Because the Basis is \r\nUnconstitutional, A Congress Member has the Right to Resign, Taxpayer’s \r\nStatement of Assets and Liabilities well-intention, But..., The Constitution \r\nDoes Not Protect the Speech of Hate, Presidential Liability for P7 B Fund \r\nJuggling, The VFA in Constitutional and International Law, The VFA in the Law of \r\nBelligerency, The VFA in Contemporary International Relations, The P3.6 B Police \r\nDrug Test Deal is Grossly Disadvantageous to the Government, The Kingdom of \r\nDarkness, or the Empire that Government Contracts Built. \r\n<P>· Senator Santiago met 365 tricycle drivers of Philcoa in Quezon City where \r\nshe distributed sacks of rice as part of her outreach program. <BR>&nbsp; \r\n<P><B><FONT size=+1>LAWS PASSED, CO-AUTHORED BY SEN. MDS</FONT></B> \r\n<P>R. A. No.&nbsp;8750, a.k.a. the “Seat Belt Law” \r\n<P><B>MIRIAMSPEAK:</B> \r\n<P>“The only thing we are not importing is politicians, because that is what we \r\nhave in abundance.” <BR>---Senator Santiago, lamenting that the country is \r\nimporting almost everything from rice, corn, pork, and poultry to fruits and \r\nfishery products, except politicians \r\n<P>“They have the rapacity of a crocodile and they have the epidermis of a \r\npachyderm.” <BR>---Senator Santiago, saying the administration was producing \r\n“new hybrid life forms” which she described as half-elephant, half-crocodile \r\n<P>“I fear very much that we are developing a cabinet of kleptocracy.” \r\n<BR>---Senator Santiago, pointing out that “world-class larceny” was what was \r\ntaking place in the administration \r\n<P>“I have only sympathy for the President.” <BR>---Senator Santiago, assailing \r\ncorruption in the Cabinet <BR>&nbsp; \r\n<P><B><FONT size=+1>&nbsp;WHAT MEDIA SAID OF MIRIAM...</FONT></B> \r\n<P>&nbsp;“Miriam has revived the glory days of the Senate as the watchdog of \r\nadministration venalities.” <BR>&nbsp;“Miriam has also shown in the Puno exposes \r\nthat she is not a Estrada stooge...In exposing Puno, Miriam has rediscovered the \r\npower of inquiry of the Senate and has become the symbol of this rediscovery at \r\na time when the legislature is so intimidated by the President’s popularity that \r\nit does not stand up to him.” <BR>&nbsp;“Miriam has found her mark---as catalyst \r\nof the reassertion by the Senate of its function as a chamber of inquiry into \r\nadministration venalities.” <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ---Amando \r\nDoronila, Inquirer <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n“Miriam finds her mark” \r\n<P>&nbsp;“Sen. Miriam Defensor Santiago is one colorful personality.&nbsp; I \r\nlike her.&nbsp; She has one of the widest, and lately most vivid vocabularies \r\namong our public figures.&nbsp; I was impressed by Santiago’s use of the English \r\nlanguage during the 1998 presidential campaign.&nbsp; She was easily the most \r\narticulate among the candidates.” <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ---Leandro \r\nV. Coronel, Inquirer <BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n“The ballistic Miriam” \r\n<P>&nbsp; “Queen of Exposes in the Philippine Senate.” \r\n<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ---Fete For A Queen, Today \r\n<P>&nbsp;Woman’s Journal:&nbsp; Who would you choose as your Valentine’s \r\ndate:&nbsp; Cindy Crawford, Miriam Defensor Santiago, or Monica Lewinsky? \r\n<BR>&nbsp;Brod Pete:&nbsp; Siyempre, si Senator Miriam.&nbsp; I’m a fan of \r\nhers.&nbsp; I like her English; it’s very English. \r\n<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ---Woman’s Journal \r\n<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 13 February 1999</P>','<P align=center><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>POLITICAL \r\nPLATFORM<BR>BY <BR>SENATOR MIRIAM DEFENSOR SANTIAGO</B></FONT></P>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>1. Stop graft and \r\ncorruption.</B></FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Design productivity \r\nincentive scheme for government employees.</FONT> </LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>2. Employment.</B> \r\n</FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Encourage actual investment \r\nand not just mere equity investment, by building factories in the countryside. \r\n</FONT></LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>3. Education. \r\n</B></FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Expand student loan \r\nservices and launch a program of state-subsidized community colleges.</FONT> \r\n</LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>4. \r\nHousing.</B></FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Mobilize out-of-school \r\nyouth on vacation, to participate in construction of low-cost mass \r\nhousing</FONT> </LI></UL>\r\n<P><FONT face=\"Arial, Helvetica, sans-serif\" size=2><B>5. Peace and Order. \r\n</B></FONT></P>\r\n<UL>\r\n<LI><FONT face=\"Arial, Helvetica, sans-serif\" size=2>Upgrade military and police \r\ncrime-fighting equipment, and offer rewards to prominently successful crime \r\nbusters.</FONT> </LI></UL>','','','[Source: <A HREF=\"http://miriam.com.ph/tcomplete.html\">http://miriam.com.ph/tcomplete.html</A>] <BR><BR>\r\n<CENTER><B><FONT face=Arial,Helvetica><FONT color=#000099><FONT size=+1>&nbsp; \r\nSEN.&nbsp; MIRIAM&nbsp; DEFENSOR&nbsp; SANTIAGO<BR><FONT size=3>A.B., Ll.B., \r\nLl.M., S.J.D., Ll.D. D.H.L.,</FONT></FONT></FONT></FONT></B> <FONT \r\nsize=3><BR><B><FONT face=Arial,Helvetica><FONT \r\ncolor=#000099>Ll.D.</FONT></FONT></B></FONT> </CENTER><FONT \r\nface=Arial,Helvetica><FONT color=#000099>&nbsp;</FONT></FONT> \r\n<P align=left><FONT face=\"Verdana, Arial, Helvetica, sans-serif\" \r\nsize=2><B>FAMILY DATA</B>: <BR><BR>Date of Birth : 15 June 1945<BR>Place of \r\nBirth : Iloilo Cit